/**
 * Represents metadata used in ArrayComponent
 */
interface ArrayMetadata {
  /**
   * The orientation of the array component, e.g., "v" for vertical
   */
  orientation?: string;

  /**
   * Whether the component should animate
   */
  anim?: boolean;
}

interface ArrayObject {
  /**
   * Sets the group data in the group history
   * @param data The group data to set
   * @param synchronize Whether to synchronize the data
   */
  group(data: number[], synchronize?: boolean): void;

  /**
   * Sets the index data in the index history
   * @param data The index data to set
   * @param synchronize Whether to synchronize the data
   */
  setIndex(data: number, synchronize?: boolean): void;

  /**
   * Sets the array data in the array history
   * @param data The array data to set
   * @param synchronize Whether to synchronize the data
   */
  setArr(data: string[], synchronize?: boolean): void;

  /**
   * Sets the frame data in the histories.
   *
   * The `data` parameter can include:
   * - `index`: The index to set in the index history.
   * - `group`: The group to set in the group history.
   * - `content`: The content to set in the array history.
   *
   * Each field is optional. If a field is provided, it will update the respective history. If not, the existing data for that field will remain unchanged.
   *
   * @param data - The data to set in the histories. It can have the following properties:
   *   - `index` (optional): A number representing the index to be set.
   *   - `group` (optional): An array of numbers representing the group to be set.
   *   - `content` (optional): An array of strings representing the content to be set.
   * @param synchronize - Whether to synchronize the data. Defaults to `true`.
   */
  frame(data: { index?: number; group?: number[]; content?: string[] }, synchronize?: boolean): void;
}

type orientation = "v" | "";

type ArraySettings = {
  orientation?: orientation;
};

declare const createArray: (settings: ArraySettings) => ArrayObject;

/**
 * Represents a label object
 */
interface LabelObject {
  /**
   * Sets the contents of label
   * @param text The contents of label
   */
  text(text: string): void;
}

interface LabelSettings {
  prefix: string;
  suffix: string;
}

/**
 * @returns A new LabelObject instance.
 */
declare const createLabel: (settings: LabelSettings) => LabelObject;

/**
 * Represents the colors used in a matrix.
 * Maps keys to color values.
 */
declare interface MatrixColor {
  [key: number]: string; // Maps numerical indices to color strings
}
/**
 * Represents a position in a 2D space.
 * Tuple with X and Y coordinates.
 */
declare type Position = [number, number];

/**
 * Represents a matrix object with various methods.
 */
declare interface MatrixObject {
  /**
   * Replaces a value at the given position in the matrix.
   * @param position The position where the replacement occurs.
   * @param value The new value to set.
   * @param synchronize Optional flag to indicate if synchronization is needed.
   */
  replace(position: Position, value: string, synchronize?: boolean): void;

  /**
   * Sets the content of the matrix.
   * @param data 2D array of strings to set as the matrix content.
   * @param synchronize Optional flag to indicate if synchronization is needed.
   */
  content(data: string[][], synchronize?: boolean): void;

  /**
   * Sets the colors for the matrix.
   * @param colors An object mapping indices to color values.
   * @param synchronize Optional flag to indicate if synchronization is needed.
   */
  colors(colors: MatrixColor, synchronize?: boolean): void;

  /**
   * Sets the grouping for the matrix.
   * @param data An array of tuples representing groups.
   * @param synchronize Optional flag to indicate if synchronization is needed.
   */
  group(data: Array<[number, number]>, synchronize?: boolean): void;

  /**
   * Sets the frame of the matrix.
   * @param data An object containing content, colors, and group information.
   * @param synchronize Optional flag to indicate if synchronization is needed.
   */
  frame(data: { content?: string[][]; colors?: MatrixColor; group?: Position[] }, synchronize?: boolean): void;
}

/**
 * Creates a new matrix object.
 * @returns A new MatrixObject instance.
 */
declare const createMatrix: () => MatrixObject;
